import{_ as e,c as a,o,a as t}from"./app.eff44e41.js";const m=JSON.parse('{"title":"\u524D\u8A00","description":"","frontmatter":{},"headers":[],"relativePath":"preface.md","lastUpdated":1668613999000}'),r={name:"preface.md"},p=t('<h1 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h1><p>\u4EBA\u7C7B\u5DF2\u7ECF\u4E3A\u7535\u5B50\u8BA1\u7B97\u673A\u7F16\u5199\u7A0B\u5E8F80\u591A\u5E74\u4E86\uFF0C\u4F46\u4EE4\u4EBA\u60CA\u8BB6\u7684\u662F\u5173\u4E8E\u5982\u4F55\u8BBE\u8BA1\u7A0B\u5E8F\u6216\u4F18\u79C0\u7684\u7A0B\u5E8F\u5E94\u8BE5\u662F\u4EC0\u4E48\u6837\u5B50\u7684\u8BA8\u8BBA\u5374\u5F88\u5C11\u3002\u5173\u4E8E\u8F6F\u4EF6\u5F00\u53D1\u8FC7\u7A0B\uFF08\u5982\u654F\u6377\u5F00\u53D1\uFF09\u548C\u5F00\u53D1\u5DE5\u5177\uFF08\u5982\u8C03\u8BD5\u5668\u3001\u7248\u672C\u63A7\u5236\u7CFB\u7EDF\u3001\u6D4B\u8BD5\u8986\u76D6\u5DE5\u5177\uFF09\u5DF2\u7ECF\u6709\u4E86\u76F8\u5F53\u591A\u7684\u8BA8\u8BBA\u3002\u7F16\u7A0B\u6280\u672F\uFF08\u5982\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\u548C\u51FD\u6570\u5F0F\u7F16\u7A0B\uFF09\u3001\u8BBE\u8BA1\u6A21\u5F0F\u548C\u7B97\u6CD5\u4E5F\u5DF2\u7ECF\u6709\u4E86\u5168\u9762\u7684\u5206\u6790\u3002\u8FD9\u4E9B\u8BA8\u8BBA\u90FD\u662F\u6709\u4EF7\u503C\u7684\uFF0C\u4F46\u662F\u5BF9\u4E8E\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u6838\u5FC3\u95EE\u9898\u4ECD\u5982\u9694\u9774\u6414\u75D2\u3002David Parns\u7684\u7ECF\u5178\u8BBA\u6587\u201C\u5173\u4E8E\u53EF\u7528\u4E8E\u628A\u7CFB\u7EDF\u5206\u89E3\u4E3A\u6A21\u5757\u7684\u51C6\u5219\u201D\u53D1\u8868\u4E8E1971\u5E74\uFF0C\u4F46\u5728\u968F\u540E\u768445\u5E74\u91CC\u8F6F\u4EF6\u8BBE\u8BA1\u6C34\u5E73\u7684\u53D1\u5C55\u5E76\u6CA1\u6709\u8D85\u8FC7\u8FD9\u7BC7\u8BBA\u6587\u592A\u591A\u3002</p><p>\u8BA1\u7B97\u673A\u79D1\u5B66\u4E2D\u6700\u57FA\u672C\u7684\u95EE\u9898\u662F\u95EE\u9898\u5206\u89E3\uFF1A\u5982\u4F55\u628A\u4E00\u4E2A\u590D\u6742\u95EE\u9898\u5206\u89E3\u6210\u591A\u4E2A\u53EF\u72EC\u7ACB\u89E3\u51B3\u7684\u5C0F\u5757\u3002\u95EE\u9898\u5206\u89E3\u662F\u7A0B\u5E8F\u5458\u6BCF\u5929\u8981\u9762\u5BF9\u7684\u4E3B\u8981\u8BBE\u8BA1\u5DE5\u4F5C\uFF0C\u7136\u800C\u9664\u4E86\u6B64\u5904\u63CF\u8FF0\u7684\u5DE5\u4F5C\u4E4B\u5916\uFF0C\u5728\u4EFB\u4F55\u4E00\u6240\u5927\u5B66\u91CC\u6211\u90FD\u6CA1\u6709\u627E\u5230\u4E00\u95E8\u4EE5\u95EE\u9898\u5206\u89E3\u4E3A\u6838\u5FC3\u4E3B\u9898\u7684\u72EC\u7ACB\u8BFE\u7A0B\u3002\u6211\u4EEC\u6559\u6388\u5FAA\u73AF\u548C\u9762\u5411\u5BF9\u8C61\u7F16\u7A0B\uFF0C\u4F46\u4E0D\u662F\u8F6F\u4EF6\u8BBE\u8BA1\u3002</p><p>\u6B64\u5916\uFF0C\u7A0B\u5E8F\u5458\u4E4B\u95F4\u5728\u8D28\u91CF\u548C\u751F\u4EA7\u529B\u4E0A\u5B58\u5728\u5DE8\u5927\u5DEE\u5F02\uFF0C\u4F46\u6211\u4EEC\u5F88\u5C11\u53BB\u4E86\u89E3\u662F\u4EC0\u4E48\u4F7F\u4F18\u79C0\u7684\u7A0B\u5E8F\u5458\u53D8\u5F97\u5982\u6B64\u4F18\u79C0\u6216\u8005\u5982\u4F55\u5728\u6211\u4EEC\u7684\u8BFE\u5802\u4E0A\u6559\u6388\u8FD9\u4E9B\u6280\u80FD\u3002\u6211\u66FE\u4E0E\u6570\u4F4D\u6211\u8BA4\u4E3A\u662F\u51FA\u8272\u7684\u7A0B\u5E8F\u5458\u4EA4\u8C08\uFF0C\u4F46\u662F\u4ED6\u4EEC\u591A\u6570\u96BE\u4EE5\u9610\u660E\u662F\u4EC0\u4E48\u7279\u5B9A\u7684\u6280\u672F\u8D4B\u4E88\u4ED6\u4EEC\u4F18\u52BF\u3002\u8BB8\u591A\u4EBA\u8BA4\u4E3A\u8F6F\u4EF6\u8BBE\u8BA1\u6280\u80FD\u662F\u5929\u751F\u7684\u5929\u8D4B\u65E0\u6CD5\u4F20\u6388\u3002\u7136\u800C\u6709\u76F8\u5F53\u591A\u7684\u79D1\u5B66\u8BC1\u636E\u8868\u660E\u8BB8\u591A\u9886\u57DF\u7684\u6770\u51FA\u8868\u73B0\u66F4\u591A\u5730\u4E0E\u9AD8\u8D28\u91CF\u7684\u5B9E\u8DF5\u6709\u5173\uFF0C\u800C\u4E0D\u662F\u5148\u5929\u7684\u80FD\u529B\uFF08\u53C2\u770B\uFF0C\u4F8B\u5982\uFF0CGeoff Colvin \u8457\u300A\u54EA\u6765\u7684\u5929\u624D\uFF1F\u7EC3\u4E60\u4E2D\u7684\u5E73\u51E1\u4E0E\u4F1F\u5927\u300B\uFF09\u3002</p><p>\u591A\u5E74\u6765\u8FD9\u4E9B\u95EE\u9898\u4E00\u76F4\u8BA9\u6211\u611F\u5230\u56F0\u60D1\u548C\u6CAE\u4E27\u3002\u6211\u60F3\u77E5\u9053\u8F6F\u4EF6\u8BBE\u8BA1\u662F\u5426\u53EF\u4EE5\u88AB\u6559\u6388\uFF0C\u5E76\u5047\u8BBE\u8BBE\u8BA1\u6280\u80FD\u533A\u5206\u5F00\u4E86\u4F18\u79C0\u7A0B\u5E8F\u5458\u4E0E\u666E\u901A\u7A0B\u5E8F\u5458\u3002\u6211\u6700\u7EC8\u8BA4\u5B9A\u56DE\u7B54\u8FD9\u4E9B\u95EE\u9898\u7684\u552F\u4E00\u65B9\u6CD5\u662F\u5C1D\u8BD5\u6559\u6388\u4E00\u95E8\u5173\u4E8E\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u8BFE\u7A0B\u3002\u8FD9\u95E8\u8BFE\u7A0B\u662F\u65AF\u5766\u798F\u5927\u5B66\u7684 CS 190 \u3002\u5728\u8FD9\u95E8\u8BFE\u4E0A\u6211\u63D0\u51FA\u4E86\u4E00\u5957\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u539F\u5219\u3002\u7136\u540E\u5B66\u751F\u901A\u8FC7\u4E00\u7CFB\u5217\u7684\u9879\u76EE\u6765\u7406\u89E3\u548C\u5B9E\u8DF5\u8FD9\u4E9B\u539F\u5219\u3002\u8BFE\u7A0B\u7684\u6388\u8BFE\u65B9\u5F0F\u7C7B\u4F3C\u4E8E\u4F20\u7EDF\u7684\u82F1\u8BED\u5199\u4F5C\u8BFE\u3002\u5728\u82F1\u8BED\u8BFE\u4E0A\u5B66\u751F\u8FD0\u7528\u4E00\u79CD\u8FED\u4EE3\u8FC7\u7A0B\uFF08\u5199\u8349\u7A3F\uFF0C\u83B7\u5F97\u53CD\u9988\uFF0C\u7136\u540E\u91CD\u5199\uFF09\u6765\u83B7\u5F97\u63D0\u5347\u3002\u5728 CS 190 \u8BFE\u7A0B\u4E2D\uFF0C\u5B66\u751F\u4ECE\u96F6\u5F00\u59CB\u5F00\u53D1\u4E00\u6B3E\u771F\u5B9E\u7684\u8F6F\u4EF6\u3002\u63A5\u7740\u6211\u4EEC\u901A\u8FC7\u5168\u9762\u7684\u4EE3\u7801\u68C0\u89C6\u6765\u8BC6\u522B\u51FA\u8BBE\u8BA1\u95EE\u9898\u3002\u7136\u540E\u5B66\u751F\u4FEE\u6539\u4ED6\u4EEC\u7684\u9879\u76EE\u6765\u4FEE\u590D\u8FD9\u4E9B\u95EE\u9898\u3002\u8FD9\u6837\u5B66\u751F\u53EF\u4EE5\u770B\u5230\u5982\u4F55\u901A\u8FC7\u5E94\u7528\u8BBE\u8BA1\u539F\u5219\u6765\u6539\u8FDB\u4ED6\u4EEC\u7684\u4EE3\u7801\u3002</p><p>\u6211\u73B0\u5728\u5DF2\u7ECF\u6559\u8FC7\u8FD9\u95E8\u8F6F\u4EF6\u8BBE\u8BA1\u8BFE\u7A0B\u591A\u6B21\uFF0C\u8FD9\u672C\u4E66\u662F\u57FA\u4E8E\u8BFE\u5802\u4E0A\u51FA\u73B0\u7684\u8BBE\u8BA1\u539F\u5219\u3002\u8FD9\u4E9B\u539F\u5219\u662F\u76F8\u5F53\u9AD8\u7684\u6C34\u5E73\u5E76\u4E14\u63A5\u8FD1\u54F2\u5B66\uFF08\u201C\u5B9A\u4E49\u4E0D\u5B58\u5728\u7684\u9519\u8BEF\u201D\uFF09\uFF0C\u6240\u4EE5\u8FD9\u5F88\u96BE\u8BA9\u5B66\u751F\u4EE5\u62BD\u8C61\u7684\u65B9\u5F0F\u7406\u89E3\u8FD9\u4E9B\u7406\u5FF5\u3002\u5B66\u751F\u6700\u597D\u7684\u5B66\u4E60\u65B9\u5F0F\u662F\u901A\u8FC7\u7F16\u5199\u4EE3\u7801\uFF0C\u72AF\u4E86\u9519\uFF0C\u7136\u540E\u7406\u89E3\u4ED6\u4EEC\u4E3A\u4F55\u72AF\u9519\u5E76\u6839\u636E\u539F\u5219\u505A\u540E\u7EED\u4FEE\u590D\u3002</p><p>\u6B64\u65F6\u4F60\u5F88\u53EF\u80FD\u5728\u60F3\uFF1A\u662F\u4EC0\u4E48\u4F7F\u6211\u8BA4\u4E3A\u81EA\u5DF1\u77E5\u9053\u6709\u5173\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u6240\u6709\u7B54\u6848\uFF1F\u5766\u8BDA\u5730\u8BF4\uFF0C\u6211\u4E0D\u77E5\u9053\u3002\u6211\u5B66\u4E60\u7F16\u7A0B\u7684\u65F6\u5019\u6CA1\u6709\u4EFB\u4F55\u5173\u4E8E\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u8BFE\u7A0B\uFF0C\u800C\u4E14\u6211\u4E5F\u4ECE\u6765\u6CA1\u6709\u5BFC\u5E08\u6765\u6559\u6211\u8BBE\u8BA1\u539F\u5219\u3002\u5728\u6211\u5B66\u4E60\u7F16\u7A0B\u90A3\u65F6\uFF0C\u4EE3\u7801\u68C0\u89C6\u51E0\u4E4E\u662F\u6CA1\u6709\u3002\u6211\u5173\u4E8E\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u601D\u60F3\u6765\u81EA\u4E8E\u4E2A\u4EBA\u7F16\u5199\u548C\u9605\u8BFB\u4EE3\u7801\u7684\u7ECF\u9A8C\u3002\u5728\u6211\u7684\u804C\u4E1A\u751F\u6DAF\u4E2D\u6211\u5DF2\u7ECF\u7528\u591A\u79CD\u8BED\u8A00\u5199\u4E86\u5927\u7EA6250000\u884C\u4EE3\u7801\u3002\u6211\u66FE\u5728\u591A\u4E2A\u56E2\u961F\u5DE5\u4F5C\u8FC7\uFF0C\u4ECE\u96F6\u521B\u5EFA\u4E09\u4E2A\u64CD\u4F5C\u7CFB\u7EDF\uFF0C\u591A\u4E2A\u6587\u4EF6\u4E0E\u5B58\u50A8\u7CFB\u7EDF\uFF0C\u57FA\u7840\u8BBE\u65BD\u5DE5\u5177\u4F8B\u5982\u8C03\u8BD5\u5668\u3001\u6784\u5EFA\u7CFB\u7EDF\u3001\u56FE\u5F62\u7528\u6237\u754C\u9762\u5DE5\u5177\u5305\uFF0C\u4E00\u79CD\u811A\u672C\u8BED\u8A00\uFF0C\u8FD8\u6709\u7528\u4E8E\u6587\u672C\u3001\u7ED8\u56FE\u3001\u6F14\u793A\u548C\u96C6\u6210\u7535\u8DEF\u7684\u4EA4\u4E92\u5F0F\u7F16\u8F91\u5668\u3002\u5728\u8FD9\u8FC7\u7A0B\u4E2D\uFF0C\u6211\u4EB2\u8EAB\u7ECF\u5386\u4E86\u5927\u578B\u7CFB\u7EDF\u7684\u95EE\u9898\uFF0C\u5E76\u5C1D\u8BD5\u4E86\u8BB8\u591A\u8BBE\u8BA1\u6280\u672F\u3002\u6B64\u5916\uFF0C\u6211\u9605\u8BFB\u4E86\u5927\u91CF\u4ED6\u4EBA\u7F16\u5199\u7684\u4EE3\u7801\uFF0C\u8FD9\u4F7F\u6211\u63A5\u89E6\u5230\u4E86\u5404\u79CD\u5404\u6837\u7684\u65B9\u6CD5\uFF0C\u6709\u597D\u7684\u4E5F\u6709\u574F\u7684\u3002</p><p>\u4ECE\u6240\u6709\u8FD9\u4E9B\u7ECF\u9A8C\u4E2D\uFF0C\u6211\u5C1D\u8BD5\u63D0\u53D6\u907F\u514D\u9519\u8BEF\u548C\u4F7F\u7528\u6280\u5DE7\u7684\u5171\u901A\u70B9\u3002\u672C\u4E66\u662F\u6211\u5B9E\u8DF5\u7ECF\u9A8C\u7684\u53CD\u6620\uFF1A\u8FD9\u91CC\u63CF\u8FF0\u7684\u6BCF\u4E2A\u95EE\u9898\u90FD\u662F\u6211\u4EB2\u8EAB\u7ECF\u5386\u7684\uFF0C\u6BCF\u79CD\u5EFA\u8BAE\u7684\u6280\u672F\u90FD\u5728\u6211\u81EA\u5DF1\u7684\u7F16\u7801\u4E2D\u6210\u529F\u5E94\u7528\u8FC7\u3002</p><p>\u6211\u4E0D\u5E0C\u671B\u8FD9\u672C\u4E66\u6210\u4E3A\u5BF9\u4E8E\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u5B9A\u8BBA\uFF1B\u6211\u76F8\u4FE1\u6211\u4F1A\u9519\u8FC7\u4E00\u4E9B\u6709\u4EF7\u503C\u7684\u6280\u672F\uFF0C\u5E76\u4E14\u4ECE\u957F\u8FDC\u6765\u770B\u6211\u7684\u4E00\u4E9B\u5EFA\u8BAE\u53EF\u80FD\u4F1A\u53D8\u6210\u574F\u4E3B\u610F\u3002\u4E0D\u7BA1\u600E\u6837\uFF0C\u6211\u5E0C\u671B\u8FD9\u672C\u4E66\u53EF\u4EE5\u5F00\u542F\u5173\u4E8E\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u8BA8\u8BBA\u3002\u5C06\u672C\u4E66\u4E2D\u63CF\u8FF0\u7684\u601D\u60F3\u4E0E\u60A8\u81EA\u5DF1\u7684\u7ECF\u9A8C\u8FDB\u884C\u6BD4\u8F83\uFF0C\u5E76\u81EA\u884C\u8BC4\u4F30\u672C\u4E66\u63CF\u8FF0\u7684\u65B9\u6CD5\u662F\u5426\u786E\u5B9E\u80FD\u964D\u4F4E\u8F6F\u4EF6\u590D\u6742\u5EA6\u3002\u8FD9\u672C\u4E66\u662F\u4E00\u4E2A\u89C2\u70B9\u96C6\uFF0C\u6240\u4EE5\u6709\u4E9B\u8BFB\u8005\u4F1A\u4E0D\u540C\u610F\u6211\u7684\u4E00\u4E9B\u5EFA\u8BAE\u3002\u5982\u679C\u60A8\u4E0D\u540C\u610F\uFF0C\u8BF7\u5C1D\u8BD5\u7406\u89E3\u539F\u56E0\u3002\u6211\u5F88\u6709\u5174\u8DA3\u4E86\u89E3\u54EA\u4E9B\u5BF9\u60A8\u6709\u6548\uFF0C\u54EA\u4E9B\u65E0\u6548\uFF0C\u4EE5\u53CA\u60A8\u5BF9\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u5176\u4ED6\u4EFB\u4F55\u60F3\u6CD5\u3002\u6211\u5E0C\u671B\u63A5\u8E35\u800C\u6765\u7684\u8BA8\u8BBA\u4F1A\u63D0\u5347\u6211\u4EEC\u5BF9\u8F6F\u4EF6\u8BBE\u8BA1\u7684\u6574\u4F53\u6C34\u5E73\u3002\u6211\u4F1A\u628A\u6211\u6240\u5B66\u4E60\u5230\u7684\u7EB3\u5165\u8FD9\u672C\u4E66\u7684\u672A\u6765\u7248\u672C\u3002\u4E0E\u6211\u4EA4\u6D41\u8FD9\u672C\u4E66\u7684\u6700\u597D\u65B9\u6CD5\u662F\u53D1\u9001\u7535\u5B50\u90AE\u4EF6\u5230\u4E0B\u9762\u7684\u5730\u5740\uFF1A</p><p><a href="mailto:software-design-book@googlegroups.com" target="_blank" rel="noreferrer">software-design-book@googlegroups.com</a></p><p>\u6211\u6709\u5174\u8DA3\u542C\u5230\u5173\u4E8E\u8FD9\u672C\u4E66\u7684\u5177\u4F53\u53CD\u9988\uFF0C\u6BD4\u5982\u9519\u8BEF\u6216\u6539\u8FDB\u5EFA\u8BAE\uFF0C\u4EE5\u53CA\u4E0E\u8F6F\u4EF6\u8BBE\u8BA1\u76F8\u5173\u7684\u4E00\u822C\u770B\u6CD5\u548C\u7ECF\u9A8C\u3002\u6211\u5BF9\u53EF\u4EE5\u5728\u672C\u4E66\u672A\u6765\u7248\u672C\u4E2D\u4F7F\u7528\u7684\u4EE4\u4EBA\u4FE1\u670D\u7684\u5B9E\u4F8B\u683C\u5916\u611F\u5174\u8DA3\u3002\u597D\u7684\u5B9E\u4F8B\u9610\u660E\u4E86\u4E00\u4E2A\u91CD\u8981\u7684\u8BBE\u8BA1\u539F\u5219\uFF0C\u5E76\u4E14\u7B80\u5355\u5230\u53EF\u4EE5\u7528\u4E00\u81F3\u4E24\u4E2A\u6BB5\u843D\u6765\u89E3\u91CA\u3002\u5982\u679C\u60A8\u60F3\u67E5\u770B\u5E76\u53C2\u4E0E\u8BA8\u8BBA\uFF0C\u60A8\u53EF\u4EE5\u52A0\u5165\u8C37\u6B4C\u5C0F\u7EC4<code>software-design-book</code>\u3002</p><p>\u5982\u679C\u51FA\u4E8E\u67D0\u79CD\u539F\u56E0<code>software-design-book</code>\u8C37\u6B4C\u5C0F\u7EC4\u5728\u672A\u6765\u6D88\u5931\u4E86\uFF0C\u8BF7\u5728\u7F51\u4E0A\u641C\u7D22\u6211\u7684\u4E3B\u9875\u3002\u4E3B\u9875\u4E0A\u4F1A\u66F4\u65B0\u5982\u4F55\u4EA4\u6D41\u672C\u4E66\u7684\u8BF4\u660E\u3002\u8BF7\u4E0D\u8981\u5411\u6211\u7684\u4E2A\u4EBA\u90AE\u7BB1\u53D1\u9001\u4E66\u7C4D\u76F8\u5173\u7684\u90AE\u4EF6\u3002</p><p>\u6211\u5EFA\u8BAE\u60A8\u5728\u8BFB\u672C\u4E66\u65F6\u62B1\u6709\u6000\u7591\u6001\u5EA6\u3002\u603B\u4F53\u76EE\u6807\u662F\u964D\u4F4E\u590D\u6742\u5EA6\uFF1B\u8FD9\u6BD4\u60A8\u5728\u672C\u4E66\u9605\u8BFB\u7684\u4EFB\u4F55\u7279\u5B9A\u7684\u539F\u7406\u6216\u601D\u60F3\u90FD\u66F4\u4E3A\u91CD\u8981\u3002\u5982\u679C\u60A8\u5C1D\u8BD5\u4E00\u4E2A\u4ECE\u672C\u4E66\u4E2D\u83B7\u5F97\u7684\u60F3\u6CD5\uFF0C\u7136\u540E\u53D1\u73B0\u4ED6\u5B9E\u9645\u4E0A\u5E76\u4E0D\u80FD\u964D\u4F4E\u590D\u6742\u5EA6\uFF0C\u90A3\u4E48\u60A8\u5C31\u4E0D\u5FC5\u518D\u7EE7\u7EED\u4F7F\u7528\u5B83\uFF08\u4F46\u662F\u8BF7\u544A\u8BC9\u6211\u4F60\u7684\u7ECF\u9A8C\uFF0C\u6211\u60F3\u83B7\u5F97\u662F\u4EC0\u4E48\u6709\u6548\u4E0E\u65E0\u6548\u7684\u53CD\u9988\uFF09\u3002</p><p>\u672C\u4E66\u8D28\u91CF\u7684\u63D0\u5347\u5F97\u76CA\u4E8E\u5927\u5BB6\u63D0\u51FA\u7684\u6279\u8BC4\u4E0E\u5EFA\u8BAE\u3002\u4EE5\u4E0B\u4EBA\u5458\u5BF9\u672C\u4E66\u7684\u8349\u7A3F\u63D0\u4F9B\u4E86\u6709\u7528\u7684\u610F\u89C1\uFF1AJeff Dean, Sanjay Ghemawat, John Hartman, Brian Kernighan, James Koppel, Amy Ousterhout, Kay Ousterhout, Rob Pike, Partha Ranganathan, Keith Schwartz, \u8FD8\u6709 Alex Snaps\u3002Christos Kozyrakis \u5EFA\u8BAE\u4F7F\u7528\u4FEE\u9970\u8BCD\u201C\u6DF1\u201D\u4E0E\u201C\u6D45\u201D\u6765\u63CF\u8FF0\u7C7B\u548C\u63A5\u53E3\uFF0C\u53D6\u4EE3\u4E86\u4E4B\u524D\u7684\u6709\u4E9B\u6B67\u4E49\u7684\u4FEE\u9970\u8BCD\u201C\u539A\u201D\u4E0E\u201C\u8584\u201D\u3002\u6211\u975E\u5E38\u611F\u8C22\u6765\u4E0A CS 190 \u8BFE\u7A0B\u7684\u5B66\u751F\uFF1B\u9605\u8BFB\u4ED6\u4EEC\u7684\u4EE3\u7801\u5E76\u4E0E\u4ED6\u4EEC\u8BA8\u8BBA\u7684\u8FC7\u7A0B\u5E2E\u52A9\u6211\u5177\u8C61\u4E86\u6211\u5BF9\u8BBE\u8BA1\u7684\u601D\u7D22\u3002</p>',14),s=[p];function n(c,i,d,_,h,f){return o(),a("div",null,s)}const g=e(r,[["render",n]]);export{m as __pageData,g as default};
